// babel 로 컴파일할 코드 
    // ES6 문법을 쓸 것 임 
    // EX) 화살표 함수 등이 들어가게 


const test = (msg) => {
    const arr = [1,2,3];
    const arr2 = [4,5,6];

    // 스프레드 연산자 ES6
        const arr3 = [...arr, ...arr2];

    // 템플릿 리터럴 ES6
        console.log(...arr3 , msg);
}

test("문자 ㄱㄱ")
    // 이걸 바벨로 변환하면 어떻게 되는지 보자 
    